import "./PokeCard.css";
import { getPokemon } from "./PokeLi";
import React, { useState, useEffect } from "react";

let PokemonWhatever = [];

function PokeCard() {
  const [LiPokemon, setLiPokemon] = useState([]);

  const handlepokemon = (newPokemon) => {
    //setLiPokemon([...LiPokemon,newPokemon])
    PokemonWhatever = [...PokemonWhatever, newPokemon];

    console.log(PokemonWhatever);
  };

  useEffect(() => {
    Array(150)
      .fill()
      .map((_, index) => {
        const pokemon = getPokemon(index + 1, handlepokemon);
        return pokemon;
      });
  }, []);

  // class Pokemon {
  //   elementTypes = "fire";
  //   name = "Ronaldo";
