import React, { useState, useEffect } from "react";


const api = axios.create({

    baseURL:`https://pokeapi.co/api/v2`


})

async function getPokemon(id) {
    try {
      const response = await api.get(`/pokemon/${id}`);
      console.log(response)
      return response;
    } catch (error) {
      console.error(error);
    }
  }

export {getPokemon}
