import "./PokeCard.css";
import { getPokemon } from "./PokeLi";
import React, { useState, useEffect } from "react";

let PokemonWhatever = []


function PokeCard() {
  

  
    
  const [LiPokemon, setLiPokemon] = useState([]);
  
  const handlepokemon =  (newPokemon) => {

    //setLiPokemon([...LiPokemon,newPokemon]) 
    PokemonWhatever = [...PokemonWhatever,newPokemon]


   
  }
  
  

  useEffect(() => {
   
      Array(150)
        .fill()
        .map( (_, index) => {
         const pokemon =  getPokemon(index + 1,handlepokemon);
         return pokemon
        })
        
    ;
  }, []); // Only re-run the effect if count changes

  class Pokemon {
    elementTypes = "fire";
    name = "Ronaldo";
    id = "4";
    ClassNames = "card";
    CardSubtitle = "Plant|Poisonous";

    image = () => {
      return `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pokemon.id}.png`;
    };
  }

  const pokemon = new Pokemon();

  return (
    <li className="card ice">
      <img class="card-image " alt={pokemon.name} src={pokemon.image()} />
      <h2 class="card-title">
        {" "}
        {pokemon.id}. {pokemon.name}
      </h2>
      <p class="card-subtitle">{pokemon.CardSubtitle}</p>
    </li>
  );
}

export default PokeCard;
