import { Button, Input } from "semantic-ui-react";


function Botoes(props) {


    let PokemonWhatever = [...props.PokemonWhatever];
    let TypeWhatever = [...props.TypeWhatever];

    (

<div className="buttoncontainer ">
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              fillPokemon()
              
            }}
          >
            Todos
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("normal", handletype);
            }}
          >
            Normal
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("water", handletype);
            }}
            color="blue"
          >
            Agua
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("fire", handletype);
            }}
            color="red"
          >
            Fogo
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("grass", handletype);
            }}
            color="green"
          >
            Planta
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("electric", handletype);
            }}
            color="yellow"
          >
            Raio
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("ice", handletype);
            }}
            color="blue"
          >
            Gelo
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("flying", handletype);
            }}
            color="blue"
          >
            Voador
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("ground", handletype);
            }}
            color="brown"
          >
            Terra
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("rock", handletype);
            }}
            color="brown"
          >
            Pedra
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("stell", handletype);
            }}
            color="teal"
          >
            Aço
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("bug", handletype);
            }}
            color="green"
          >
            Inseto
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("dragon", handletype);
            }}
            color="purple"
          >
            Dragão
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("fairy", handletype);
            }}
            color="pink"
          >
            Fada
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("poison", handletype);
            }}
            color="purple"
          >
            Venenoso
          </Button>
          <Button
            className="item"
            onClick={() => {
              PokemonWhatever = [];
              TypeWhatever = [];
              getType("fighting", handletype);
            }}
            color="blue"
          >
            Lutador
          </Button>
        </div>

    )

}

export default Botoes;
