import { getPokemon } from "./PokeLi";
import React, { useState } from "react";
//import "./Status.module.css"

let PokemonWhatever = [];

function Status() {
  
    const [pokemonlist, setpokemonlist] = useState([]);

    const handlepokemon = (newPokemon) => {
    PokemonWhatever = [...PokemonWhatever, newPokemon];

    //console.log(PokemonWhatever);
    // PokemonWhatever.sort((a, b) => {
    //   return a.id - b.id;
    // });

    //console.log(PokemonWhatever);
    if (PokemonWhatever.length === 1) {
      setpokemonlist(PokemonWhatever);
    }
  };

  function fillPokemon() {
    Array(1)
      .fill()
      .map((_, index) => {
        const pokemon = getPokemon(3, handlepokemon);
        return pokemon;
      });
  }

  fillPokemon()

  const tentei = (pokemonlist.map((i) => {
      const elementTypes = i.types.map((typeInfo) => typeInfo.type.name);
      return i.name
  }))

  console.log(tentei)


   
  

  return (
    <div class="container">
    <div class="left-screen">
      <div class="left-screen__top">
        <div class="light-container">
          <div class="light light--blue">
          </div>
        </div>
        <div class="light light--red"></div>
        <div class="light light--yellow"></div>
        <div class="light light--green"></div>
      </div>
      <div class="left-screen__bottom">
        <div class="main-screen">
          <div class="main-screen__top-lights">
          </div>
          <div id="display" class="main-screen__display">
            <div class="pokemon-image"></div>
            <div class="search-message">Searching...</div>
            <div class="not-found-message">Pokemon Not Found</div>
          </div>
          <div class="main-screen__speaker-light"></div>
          <div class="main-screen__speaker">
            <div></div>
            <div></div>
            <div></div>
            <div></div>
          </div>
        </div>
      </div>
      <div class="left-screen__joint">
        <div class="joint"></div>
        <div class="joint"></div>
        <div class="joint"></div>
        <div class="joint"></div>
        <div class="joint__reflextion"></div>
      </div>
    </div>
    <div class="right-screen">
      <div class="right-screen__top">
        <div></div>
      </div>
      <div class="right-screen__bottom">
        <div class="info-container">
          <input id="search" type="text" class="info-input" placeholder="Search Pokemon Name or ID"></input>
          <section class="info-screen">
            <div id="species" class="info">
              <div class="label">Species:</div>
              <div class="desc">...</div>
            </div>
            <div id="type" class="info">
              <div class="label">Type:</div>
              <div class="desc">...</div>
            </div>
            <div id="height" class="info">
              <div class="label">Height:</div>
              <div class="desc">...</div>
            </div>
            <div id="weight" class="info">
              <div class="label">Weight:</div>
              <div class="desc">...</div>
            </div>
            <div id="evolution" class="info">
              <div class="label">Evolution Chain:</div>
              <div class="desc">...</div>
            </div>
            <div id="bio" class="info">
              <div class="label">Biology:</div>
              <div class="desc">...</div>
            </div>
          </section>
        </div>
      </div>
    </div>
  </div>
  
  )
}

export default Status;
